import React from 'react';
import { View, Image, StyleSheet } from 'react-native';

const TriangleImages = () => {
  return (
    <View style={styles.container}>
      <Image
        style={styles.topImage}
        source={require('./image1.jpg')}
        onPress={() => console.log('image 1 pressée')}
      />
      <View style={styles.bottomRow}>
        <Image
          style={styles.bottomImage}
          source={require('./image2.jpg')}
          onPress={() => console.log('image 2 pressée')}
        />
        <Image
          style={styles.bottomImage}
          source={require('./image3.jpg')}
          onPress={() => console.log('image 3 pressée')}
        />
        <Image
          style={styles.bottomImage}
          source={require('./image4.jpg')}
          onPress={() => console.log('image 4 pressée')}
        />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  topImage: {
    width: '80%',
    height: '40%',
    marginBottom: 20,
  },
  bottomRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
  bottomImage: {
    width: '30%',
    height: '50%',
  },
});

export default TriangleImages;
